using Microsoft.AspNetCore.Mvc;using Project_Backend_2024.DTO;using BudgetTracker.Models;namespace BudgetTracker.Controllers{[ApiController][Route("api/[controller]")]public class ExpensesController:ControllerBase{static readonly List<Expense> store=new();[HttpGet]public IActionResult Get()=>Ok(store);[HttpPost]public IActionResult Post([FromBody]Expense e){store.Add(e);return Created("",e);} }}