using Microsoft.AspNetCore.Mvc;using Project_Backend_2024.DTO;using BudgetTracker.Models;namespace BudgetTracker.Controllers{[ApiController][Route("api/[controller]")]public class GoalsController:ControllerBase{static readonly List<Goal> store=new();[HttpGet]public IActionResult Get()=>Ok(store);[HttpPost]public IActionResult Post([FromBody]Goal g){store.Add(g);return Created("",g);} }}